'with players_with_aces as (',
'      select c.id course_id, c.course_mode, r.player_id',
'        from wmg_rounds_unpivot_mv r',
'           , wmg_courses c',
'       where r.course_id = c.id',
'         and r.week = :P100_WEEK',
'         and r.score = 1',
')',
'select ''Best score '' || decode(course_mode, ''E'', ''Easy'', ''Hard'') result, listagg(player_name, '', '') who, total',
'from (',
'      select r.*, rank() over (partition by course_mode order by r.total) rn',
'      from (',
'      select course_mode, player_id, player_name, sum(under_par) total',
'        from wmg_rounds_v',
'        where week = :P100_WEEK ',
'       group by course_mode, player_id, player_name',
'      ) r',
'      order by course_mode, rn, r.player_name',
')',
'where rn = 1',
'group by  course_mode, total',
'union all',
'select result, listagg(who, '', '') who, total',
'from (',
'    select result, who, total, rank() over (order by total desc) rn',
'    from (',
'    select ''<i class="em em-large_blue_diamond" aria-role="presentation" aria-label="LARGE BLUE DIAMOND"></i> ''',
'        || ''Player(s) with most Aces'' result',
'         , r.player_name who',
'         , sum(decode(s1, 1, 1, 0)',
'             + decode(s2, 1, 1, 0)',
'             + decode(s3, 1, 1, 0)',
'             + decode(s4, 1, 1, 0)',
'             + decode(s5, 1, 1, 0)',
'             + decode(s6, 1, 1, 0)',
'             + decode(s7, 1, 1, 0)',
'             + decode(s8, 1, 1, 0)',
'             + decode(s9, 1, 1, 0)',
'             + decode(s10, 1, 1, 0)',
'             + decode(s11, 1, 1, 0)',
'             + decode(s12, 1, 1, 0)',
'             + decode(s13, 1, 1, 0)',
'             + decode(s14, 1, 1, 0)',
'             + decode(s15, 1, 1, 0)',
'             + decode(s16, 1, 1, 0)',
'             + decode(s17, 1, 1, 0)',
'             + decode(s18, 1, 1, 0)',
'          ) total',
'      from wmg_rounds_v r',
'     where r.week = :P100_WEEK',
'    group by r.player_name',
'    ) aces',
'    order by aces.who',
')',
'where rn = 1',
'group by  result, total',
'union all',
'select ''Zero Aces on Hard'' result, ''- Undisclosed -'' who, count(*) total',
'from ( ',
'   select player_id',
'    from wmg_rounds_v r',
'     where r.week = :P100_WEEK',
'       and r.course_mode = ''H''',
'  minus  -- all players, minus players with aces',
'   select player_id',
'    from players_with_aces',
'   where course_mode = ''H''',
')',
'union all',
'select ''Zero Aces on Easy'' result, ''- Undisclosed -'' who, count(*) total',
'from ( ',
'   select player_id',
'    from wmg_rounds_v r',
'     where r.week = :P100_WEEK',
'       and r.course_mode = ''E''',
'  minus  -- all players, minus players with aces',
'   select player_id',
'    from players_with_aces',
'   where course_mode = ''E''',
')',
'',
''))
